        for vrstica in f:
            vrstica = vrstica.strip()
            vseznam = vrstica.split(" ")
            if len(vseznam) == 6 or len(vseznam) == 7 or 'Bosnia' in vseznam: #ime in priimek
                pozicija = POZICIJA[vseznam[4].split("\t")[1]]
                imePriimek = vseznam[3]
                tr = dobiVelikoCrko(imePriimek)
                ime = imePriimek[tr[1]:]
                priimek = imePriimek[:tr[1]]
                koncnoIme = ime + " " + priimek
            elif len(vseznam) == 4: #eno ime
                nov = vseznam[2].split('\t')
                imePriimek = nov[0]
                tr = dobiVelikoCrko(imePriimek)
                ime = imePriimek[:tr[1]]
                priimek = ime
                pozicija = POZICIJA[nov[1]]
                koncnoIme = ime
            elif len(vseznam) == 8: #veƒç kot dva imena
                nov = vseznam[6].split("\t")
                priimek = nov[0]
                ime = vseznam[2] + " " + vseznam[3]
                pozicija = POZICIJA[nov[1]]
                koncnoIme = ime + " " + priimek
            if "'" in koncnoIme:
                koncnoIme = koncnoIme.replace("'", '"')
            print(koncnoIme, pozicija, kratica)
